function testWebP(callback) {

var webP = new Image();
webP.onload = webP.onerror = function () {
callback(webP.height == 2);
};
webP.src = "data:image/webp;base64,UklGRjoAAABXRUJQVlA4IC4AAACyAgCdASoCAAIALmk0mk0iIiIiIgBoSygABc6WWgAA/veff/0PP8bA//LwYAAA";
}

testWebP(function (support) {

if (support == true) {
document.querySelector('body').classList.add('webp');
}else{
document.querySelector('body').classList.add('no-webp');
}
});

'use strict';
let sizer = '.size4';
let galleryBlock = document.querySelector('#c-gallery');

document.addEventListener("DOMContentLoaded", function() {
    imagesLoaded(galleryBlock).on('progress', function() {
        var msnry = new Masonry(galleryBlock, {
            itemSelctor: '.c-section-gallery-block__item ',
            columWidth: 2000,
            percentPosition: true,
            gutter: 6,
        });
    });
});
const lg = document.getElementById('c-gallery');

lightGallery(lg, {

    speed: 500,
    appendSubHtmlTo: false,
    closeOnTap: true,
    download: false,

})

lg.addEventListener('lgBeforeOpen', () => {
document.body.classList.add('no-scroll')
});lg.addEventListener('lgBeforeClose', () => {
document.body.classList.remove('no-scroll')
});
