const tx = document.getElementsByTagName("textarea");
for (let i = 0; i < tx.length; i++) {
  tx[i].setAttribute("style", "height:" + (tx[i].scrollHeight) + "px;overflow-y:hidden;");
  tx[i].addEventListener("input", OnInput, false);
}

function OnInput() {
  this.style.height = "auto";
  this.style.height = (this.scrollHeight) + "px";
}
let scrollPos = 0;

window.onscroll = function showHeader() {
    let header = document.querySelector('.c-header')
    let headerimg = document.querySelector('#header-img')
    let headerfixedimg = document.querySelector('#header-fixed-img')

    if ((document.body.getBoundingClientRect()).top > scrollPos) {

        if (window.pageYOffset > 100) {
            header.classList.add('c-header__fixed')
            headerimg.style.display = "none";
            headerfixedimg.style.display = "block";
        } else {
            header.classList.remove('c-header__fixed')
            headerimg.style.display = "block";
            headerfixedimg.style.display = "none";

        }
    }else{
         header.classList.remove('c-header__fixed')
            headerimg.style.display = "block";
            headerfixedimg.style.display = "none";
    }
            scrollPos = (document.body.getBoundingClientRect()).top;
}

const modalBrn = document.querySelectorAll('[data-modal]')
const modalClose = document.querySelectorAll('[data-modal-close]')
const modal = document.querySelectorAll('.c-modal')
const body = document.body

modalBrn.forEach(item => {
    item.addEventListener('click', event => {
        let $this = event.currentTarget
        let modalId = $this.getAttribute('data-modal')
        let modal = document.getElementById(modalId)
        let modalContent = modal.querySelector('.c-modal__content')


        modalContent.addEventListener('click', event => {
            event.stopPropagation()
        })

         
              modal.classList.add('show')
        body.classList.add('no-scroll')

      
        setTimeout(() => {
            modalContent.style.transform = "none"
            modalContent.style.opacity = "1"
        }, 1)


    })
})
modalClose.forEach(item => {
    item.addEventListener('click', event => {
        let currentModal = event.currentTarget.closest('.c-modal')
        closeModal(currentModal)
    })
})
modal.forEach(item => {
    item.addEventListener('click', event => {
        let currentModal = event.currentTarget.closest('.c-modal')
        closeModal(currentModal)
    })
})

function closeModal(currentModal) {
    let modalContent = currentModal.querySelector('.c-modal__content')

    setTimeout(() => {
        currentModal.classList.remove('show')
        body.classList.remove('no-scroll')
    }, 100)
        modalContent.removeAttribute('style')

}
const burger = document.getElementById('sidebarToggle')
const sidebar = document.getElementById('sidebar')

const burgerItems = document.querySelectorAll('.c-burger__item')

burger.addEventListener('click', event => {
	  sidebar.classList.toggle('active-sidebar')

	setTimeout(() =>{
    document.body.classList.toggle('active-burger')


	},1)

}) 
burgerItems.forEach(item => {
	item.addEventListener('click', event => {
		 sidebar.classList.remove('active-sidebar')
    document.body.classList.remove('active-burger')
    
})
	})


